  json('[{"id":"1","url":"https://images.unsplash.com/photo-1541432901042-2d8bd64b4a9b?q=80&w=2070","alt":{"tr":"Kapadokya sıcak hava balonları gün doğumunda","en":"Cappadocia hot air balloons at sunrise","de":"Kappadokien Heißluftballons bei Sonnenaufgang","ru":"Каппадокия воздушные шары на рассвете","pl":"Kapadocja balony na gorące powietrze o wschodzie słońca"},"isMain":true},{"id":"2","url":"https://images.unsplash.com/photo-1599946347371-68eb71b16afc?q=80&w=2070","alt":{"tr":"Kapadokya peri bacaları","en":"Cappadocia fairy chimneys","de":"Kappadokien Feenkamine","ru":"Каппадокия волшебные дымоходы","pl":"Kapadocja kominy wróżek"},"isMain":false},{"id":"3","url":"https://images.unsplash.com/photo-1605649487212-47bdab064df7?q=80&w=2070","alt":{"tr":"Göreme açık hava müzesi","en":"Göreme open air museum","de":"Göreme Freilichtmuseum","ru":"Музей под открытым небом Гёреме","pl":"Muzeum na wolnym powietrzu Göreme"},"isMain":false},{"id":"4","url":"https://images.unsplash.com/photo-1588410800576-8f2e9eeaa0d4?q=80&w=2070","alt":{"tr":"Kapadokya kayalık manzarası","en":"Cappadocia rocky landscape","de":"Kappadokien felsige Landschaft","ru":"Каппадокия скалистый пейзаж","pl":"Kapadocja skalisty krajobraz"},"isMain":false}]'),
